fn save_data(dest: &mut [u8], src: &String) {
    if src.chars().count() > INPUT_SIZE {
        println!("Упс, что-то пошло не так... Пожалуйста, попробуйте снова позже.");
        std::process::exit(1);
    }

    let mut dest_ptr = dest.as_mut_ptr() as *mut char;

    unsafe {
        for c in src.chars() {
            dest_ptr.write(c);
            dest_ptr = dest_ptr.offset(1);
        }
    }
}

fn read_user_input() -> String {
    let mut s: String = String::new();
    io::stdin().read_line(&mut s).unwrap();
    s.trim_end_matches("\n").to_string()
}

fn get_option() -> Option<MenuOption> {
    let mut input = String::new();
    io::stdin().read_line(&mut input).unwrap();

    MenuOption::from_int(input.trim().parse().expect("Неверная Опция"))
}

fn present_survey(feedback: &mut Feedback) {
    if feedback.submitted {
        println!("Опрос с этим ID уже существует.");
        return;
    }

    println!("\n\nЗдравствуйте, наша мастерская сталкивается с быстрым окислением. Поскольку мы высоко ценим здоровье и");
    println!("безопасность на рабочем месте, мы наняли службу БОЗ (Быстрая Окислительная Защита), чтобы обеспечить ");
    println!("структурную безопасность мастерской. Они хотели бы получить краткое заявление о состоянии мастерской ");
    println!("от каждого члена команды. Это полностью конфиденциально. Каждый ответ будет связан с случайным числом, ");
    println!("не связанным с вами никаким образом.                                                             \n");

    print!("Заявление (максимум 200 символов): ");
    io::stdout().flush().unwrap();
    let input_buffer = read_user_input();
    save_data(&mut feedback.statement, &input_buffer);

    println!("\n{}", "-".repeat(74));

    println!("Спасибо за ваше заявление! Мы постараемся решить проблемы как можно быстрее!\nТеперь, пожалуйста, выйдите из программы.");

    println!("{}", "-".repeat(74));

    feedback.submitted = true;
}

fn present_config_panel(pin: &u32) {
    use std::process::{self, Stdio};

    // сила пина неважна, поскольку ввод пина отключен
    if *pin != 123456 {
        println!("Неверный пин. Этот инцидент будет зарегистрирован.");
        return;
    }

    process::Command::new("/bin/sh")
        .stdin(Stdio::inherit())
        .stdout(Stdio::inherit())
        .output()
        .unwrap();
}

fn print_menu() {
    println!("\n\nДобро пожаловать в портал опроса о Быстрой Окислительной Защите!                ");
    println!("(Если вас кто-то отправил заполнить опрос, выберите пункт 1)\n");
    println!("1. Заполнить опрос");
    println!("2. Панель конфигурации");
    println!("3. Выход");
    print!("Выбор: ");
    io::stdout().flush().unwrap();
}

fn main() {
    print_banner();

    let mut feedback = Feedback {
        statement: [0_u8; INPUT_SIZE],
        submitted: false,
    };
    let mut login_pin: u32 = 0x11223344;

    loop {
        print_menu();
        match get_option().expect("Неверная Опция") {
            MenuOption::Survey => present_survey(&mut feedback),
            MenuOption::ConfigPanel => {
                if PIN_ENTRY_ENABLED {
                    let mut input = String::new();
                    print!("Введите пин-код конфигурации: ");
                    io::stdout().flush().unwrap();
                    io::stdin().read_line(&mut input).unwrap();
                    login_pin = input.parse().expect("Неверный пин-код");
                } else {
                    println!("\nВход в панель конфигурации был отключен администратором.");
                }

                present_config_panel(&login_pin);
            }
            MenuOption::Exit => break,
        }
    }
}
